import "./App.css";
import Home from "./Components/Home/Home.js";
import React , { useEffect,useState } from "react";
import { useKeycloak } from "@react-keycloak/web";
import { ReactKeycloakProvider } from "@react-keycloak/web";
import keycloak from "./keycloak";

function App() {
  const { keycloak, initialized } = useKeycloak();
    const[user,setUser]=useState('');
    React.useEffect(()=>{
        if(keycloak.authenticated)
        {
        let authToken=keycloak.token;
        setUser(keycloak.tokenParsed.given_name);
        console.log(authToken+"456");
        setAuthToken(authToken);
        }
      },[keycloak.authenticated])
      const setAuthToken=(value)=>
      {
        localStorage.setItem('authToken',value)
      }
  return (
    <>
    <ReactKeycloakProvider authClient={keycloak} initOptions={{onLoad:'login-required',checkLoginIframe: false}}>
      <div class="body1">
        <div class="top_navbar">
          <div class="logo">EduGeeks</div>
          <div class="menu">
            <div class="hamburger">
              <i class="fas fa-bars"></i>
            </div>
            <div className="logIn">
              <p>Hi {user}</p>
            </div>
            <div className="login-logout-Button">
              {!keycloak.authenticated && (
                <button className="buttn" onClick={() => keycloak.login()}>
                  LOG IN
                </button>
              )}
            </div>
          </div>
        </div>

        <Home />

      </div>
      </ReactKeycloakProvider>

    </>
  );
}

export default App;
