import React,{useState} from "react";
import "./TopNavbar.css";
import { useKeycloak } from "@react-keycloak/web";

export default function TopNavabar() {
  const  {keycloak} = useKeycloak();
  const [user, setUser] = useState("");
  React.useEffect(() => {
    if (keycloak.authenticated) {
      let authToken = keycloak.token;
      setUser(keycloak.tokenParsed.given_name);
      console.log(authToken + "456");
      setAuthToken(authToken);
    }
  },[keycloak.authenticated]);
  const setAuthToken = (value) => {
    localStorage.setItem("authToken", value);
  };

  return (
    <div class="body1">
      <div class="top_navbar">
        <div class="logo">EduGeeks</div>
        <div class="menu">
          <div class="hamburger">
            <i class="fas fa-bars"></i>
          </div>
          <div className="logIn">
            <p>Hi {user}</p>
          </div>
          <div className="login-logout-Button">
            {!keycloak.authenticated && (
              <button className="buttn" onClick={() => keycloak.login()}>
                LOG OUT
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}
