import React, { useState } from 'react';
import './AlumniChat.css';
import {useLocation} from 'react-router-dom';

export default function AlumniChat() {
  const[alumni,setAlumniName] = useState();
  const[msg,setMsg]  = useState();
  const studentName = "Aastha";
  const location = useLocation();
  setAlumniName(location.state.alumni);
  // const MsgRead = () => {
  //   setMsg(document.getElementById(msg).textContent);
  //   console.log(msg);
  // }

  return (
    <div className="alumniSec">
        <h1>Alumni Interaction Section</h1>
        <div className="messageSection">

        </div>
        <div className="messagePart">
            <div className='Msg'>
                <input type = "text" name = "Msg" placeholder='Enter your message here...' id="msg"></input>
            </div>
            <div className='sendMsg'>
              <button onClick={()=>setMsg(document.getElementById('msg').textContent)}>SEND</button>
            </div>
        </div>
    </div>
  )
}
