import React from 'react';
import './AlumniChat.css';
import {useLocation} from 'react-router-dom';

export default function AlumniChat() {
  const location = useLocation();
  console.log(location.state.alumni.alumniName);
  return (
    <div className="alumniSec">
        <h1>Alumni Interaction Section</h1>
        <div className="messageSection">

        </div>
        <div className="messagePart">
            <div className='Msg'>
                <input type = "text" name = "Msg" placeholder='Enter your message here...'></input>
            </div>
            <div className='sendMsg'>
              <button>SEND</button>
            </div>
        </div>
    </div>
  )
}
