import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Alumni.css";

function AlumniInteractionSection() {
  const [interactions, setInteractions] = useState([]);
  const [newMessage, setNewMessage] = useState("");
  const [selectedInteraction, setSelectedInteraction] = useState(null);

  useEffect(() => {
    getInteractions();
  }, []);

  const getInteractions = async () => {
    try {
      const response = await axios.get("http://localhost:8080/interactions");
      setInteractions(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleMessageChange = (event) => {
    setNewMessage(event.target.value);
  };

  const handleInteractionClick = (interaction) => {
    setSelectedInteraction(interaction);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post(
        `http://localhost:8080/interactions/${selectedInteraction.id}/messages`,
        {
          message: newMessage,
        }
      );
      setSelectedInteraction({
        ...selectedInteraction,
        messages: [...selectedInteraction.messages, response.data],
      });
      setNewMessage("");
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="alumni">
      <h1>Alumni Interaction Section</h1>
      <div className="interactions">
        <ul>
          {interactions.map((interaction) => (
            <li key={interaction.id} onClick={() => handleInteractionClick(interaction)}>
              <h3>
                {interaction.studentName} - {interaction.alumniName}
              </h3>
              <p>{interaction.messages[interaction.messages.length - 1]?.content}</p>
              <p>{new Date(interaction.messages[interaction.messages.length - 1]?.timestamp).toLocaleString()}</p>
            </li>
          ))}
        </ul>
      </div>
      <div className="interaction-details">
        {selectedInteraction && (
          <>
            <h3>
              {selectedInteraction.studentName} - {selectedInteraction.alumniName}
            </h3>
            <ul>
              {selectedInteraction.messages.map((message) => (
                <li key={message.id}>
                  <p>{message.content}</p>
                  <p>{new Date(message.timestamp).toLocaleString()}</p>
                </li>
              ))}
            </ul>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                placeholder="Enter a message"
                value={newMessage}
                onChange={handleMessageChange}
              />
              <button type="submit">Send</button>
            </form>
          </>
        )}
      </div>
    </div>
  );
}

export default AlumniInteractionSection;
