import React, { useState, useEffect } from "react";
import axios from "axios";

function AlumniInteractionSection() {
  const [interactions, setInteractions] = useState([]);
  const [newInteraction, setNewInteraction] = useState({ studentName: "", alumniName: "", message: "" });

  useEffect(() => {
    getInteractions();
  }, []);

  const getInteractions = async () => {
    try {
      const response = await axios.get("http://localhost:8080/interactions");
      setInteractions(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  const handleInteractionChange = (event) => {
    const { name, value } = event.target;
    setNewInteraction((prevState) => ({ ...prevState, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/interactions", newInteraction);
      setInteractions([...interactions, response.data]);
      setNewInteraction({ studentName: "", alumniName: "", message: "" });
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="alumni">
      <h2>Alumni Interaction Section</h2>
      <ul>
        {interactions.map((interaction) => (
          <li key={interaction.id}>
            <p>{interaction.studentName} &lt;--&gt; {interaction.alumniName}</p>
            <p>{interaction.message}</p>
            <p>{interaction.dateTime}</p>
          </li>
        ))}
      </ul>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="studentName"
          placeholder="Enter your name"
          value={newInteraction.studentName}
          onChange={handleInteractionChange}
        />
        <input
          type="text"
          name="alumniName"
          placeholder="Enter alumni name"
          value={newInteraction.alumniName}
          onChange={handleInteractionChange}
        />
        <input
          type="text"
          name="message"
          placeholder="Enter a message"
          value={newInteraction.message}
          onChange={handleInteractionChange}
        />
        <button type="submit">Send</button>
      </form>
    </div>
  );
}

export default AlumniInteractionSection;
