import React, { useState, useEffect } from "react";
import "./AlumniChat.css";
import qs from "qs";
import { useLocation } from "react-router-dom";
import { chat, fetchMsgs } from "../DetailsService";

export default function AlumniChat() {
  const [alumniName, setAlumniName] = useState();
  const [msgValue, setMsgValue] = useState();
  const studentName = "Aastha";
  const location = useLocation();

  
  const MsgRead = () => {
    setMsgValue(document.getElementById("enterMsg").value);
  };
  const fetchMsg = async () => {
    const alumniAndStudent = {
      studentName: "Aastha",
      alumniName: alumniName,
    };
    await fetchMsgs(qs.stringify(alumniAndStudent))
      .then((response) => {
        console.log(response);
      })
      .catch((error) => console.log(error));
  };

  useEffect(() => {
    
    setAlumniName(location.state.alumni);
    console.log(alumniName);
    fetchMsg();
  },[]);

  

  
  const SendMsg = async () => {
    const requestBodyArray = {
      studentName: studentName,
      alumniName: alumniName,
      msgValue: msgValue,
      fromToMsg: 1,
    };
    await chat(qs.stringify(requestBodyArray))
      .then((response) => {
        console.log(response);
      })
      .catch((error) => console.log(error));
    fetchMsg();
  };

  return (
    <div className="alumniSec">
      <h1>Alumni Interaction Section</h1>

      <div className="messageSection"></div>

      <div className="messagePart">
        <div className="Msg">
          <input
            type="text"
            name="Msg"
            placeholder="Enter your message here..."
            id="enterMsg"
            onChange={MsgRead}
          ></input>
        </div>
        <div className="sendMsg">
          <button onClick={SendMsg}>SEND</button>
        </div>
      </div>
    </div>
  );
}
